(class Utils {
    static makeString(length = 18) {
        let result = '';
        let characters = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789';
        for (let i = 0; i < length; i++) result += characters.charAt(~~(Math.random() * characters.length));
        return result;
    }
    static get timeout() {
        return 8000;
    }
    static promise(opt) {
        return new Promise((resolve, reject) => {
			const Authorization = `Bearer ${pm.environment.get('Authorization')}`;
			const header = {...opt.header, Authorization};
            pm.sendRequest({...opt, header}, (err, response) => {
                if (err) return reject(err);
                resolve(response);
            });
        });
    }
    static makeNumber(n = 20) {
        return ~~(Math.random() * n);
    }
    static _capitalize(s) {
        s = s.trim();
        return s && s[0].toUpperCase() + s.slice(1);
    }
    static testsHttpResponseJsonSchema(obj = {
        statusMessage: 'Created',
        statusCode: 201
    }) {
        if (!(obj instanceof Object && obj.constructor === Object)) throw new Error('Invalid params');
        else if (!Object.keys(obj).every(k => ['statusMessage', 'statusCode', 'schema', 'scope', 'errorMessage', 'errorCode'].includes(k))) throw new Error("The properties must be: 'statusMessage', 'statusCode', 'schema', 'scope', 'errorMessage', 'errorCode'");
        const defaultValues = Object.freeze({
            scope: pm.response,
            statusMessage: 'Created',
            statusCode: 201,
        });

		const defaultValuesHttp4xx = Object.freeze({
			scope: pm.response,
            errorCode: 400,
			errorMessage: 'Bad Request'
        });
        const result = obj.statusCode && obj.statusCode >= 400 && obj.statusCode < 500 ? Object.assign({}, defaultValuesHttp4xx, obj) : Object.assign({}, defaultValues, obj);
        pm.test(`Status code is ${result.statusCode}`, () => {
            pm.expect(result.scope.code).to.be.equal(result.statusCode);
        });
        pm.test(`Status message is ${this._capitalize(result.statusMessage.trim())}`, () => {
            pm.expect(result.scope.status.toLowerCase()).to.be.equal(`${result.statusMessage.trim().toLowerCase()}`);
        });
        if (result.schema) {
            pm.test('Json Schema is valid', () => {
                const Ajv = require('ajv');
                const ajv = new Ajv();
                pm.expect(ajv.validate(JSON.parse(pm.environment.get(`${result.schema.trim()}`)), result.scope.json())).to.be.true;
            });
        }
        if (result.statusCode >= 400 && result.statusCode < 500 && result.errorMessage) {
            pm.test('Validate error message', () => {
                pm.expect(result.scope.json().message.toLowerCase()).to.be.equal(result.errorMessage.trim().toLowerCase());
            });
        }
        if (result.statusCode >= 400 && result.statusCode < 500 && result.errorCode || result.errorCode === 0) {
            pm.test('Validate error code', () => {
                pm.expect(result.scope.json().error_code).to.be.equal(result.errorCode);
            });
        }
    }
    static makeLegalDocument() {
        const numbers = '0123456789';
        const chars = 'ABCDEFGHIJKLMNOPQRSTUVWXYZ';
        const threeOrFour = ~~(Math.random() * (5 - 3)) + 3;
        let res = '';
        for (let i = 0; i < (threeOrFour + 9); i++) {
            if (i < threeOrFour) res += chars.charAt(~~(Math.random() * chars.length));
            else if (i < (threeOrFour + 6)) res += numbers.charAt(~~(Math.random() * numbers.length));
            else res += `${chars}${numbers}`.charAt(~~(Math.random() * `${chars}${numbers}`.length));
        }
        return res;
    }
});

